import React from "react";
import { Button } from "@/components/ui/button";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth0 } from "@auth0/auth0-react";
import { useDispatch } from "react-redux";
import { setuser } from "../../Redux/auth.reducer";
import { useState } from "react";
import { useEffect } from "react";
import { toast } from "sonner";

const Complete = () => {
  const navigate = useNavigate();
  const { getAccessTokenSilently, user } = useAuth0();
  const [driverExp, setdriverExp] = useState("");
  const [licenceId, setlicenceId] = useState("");
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      if (!user) return;
      try {
        const res = await axios.get(
          `${import.meta.env.VITE_BASE_URL}/driver/veryfi/email/${user.email}`
        );
        dispatch(setuser(res.data.newUser));
        if (res.data.success) {
          navigate("/"); // already registered driver
        }
        toast(res.data.message);
      } catch (error) {
        console.log(
          "Verification error:",
          error.response?.data || error.message
        );
        const errorMessage =
          error.response?.data?.message || error.message || "An error occurred";
        toast.error(errorMessage);
      }
    };
    fetchData();
  }, [getAccessTokenSilently, navigate, user]);

  return (
    <>
      <div className="flex justify-center items-center absolute inset-0 gap-3 shadow-2xl ">
        <Button
          onClick={() => navigate("/Login/driver")}
          className="bg-green-500 hover:bg-green-600 cursor-pointer shadow-2xl"
        >
          login as driver
        </Button>

        <Button
          onClick={() => navigate("/Login/User")}
          className="bg-green-500 hover:bg-green-600 cursor-pointer shadow-2xl"
        >
          login as User
        </Button>
      </div>
    </>
  );
};

export default Complete;

import React, { useState, useRef } from "react";
import axios from "axios";
import { useAuth0 } from "@auth0/auth0-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { useNavigate } from "react-router-dom";
import Navbar from "../shared/Navbar";
import { toast } from "sonner";
import { useSelector } from "react-redux";
import { useTranslation } from "react-i18next";

const CreateBus = () => {
  const { getAccessTokenSilently } = useAuth0();
  const { darktheme } = useSelector((store) => store.auth);
  const { t } = useTranslation();
  const [deviceID, setDeviceID] = useState("");
  const [ticketPrice, setticketPrice] = useState("");
  const [to, setTo] = useState("");
  const [from, setFrom] = useState("");
  const [name, setName] = useState("");
  const [timeSlots, setTimeSlots] = useState([{ startTime: "", endTime: "" }]);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(null);

  // Separate states for "From" search
  const [fromSearchQuery, setFromSearchQuery] = useState("");
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [showFromSuggestions, setShowFromSuggestions] = useState(false);

  // Separate states for "To" search
  const [toSearchQuery, setToSearchQuery] = useState("");
  const [toSuggestions, setToSuggestions] = useState([]);
  const [showToSuggestions, setShowToSuggestions] = useState(false);

  const navigate = useNavigate();

  const handleTimeSlotChange = (index, field, value) => {
    const updatedSlots = [...timeSlots];
    updatedSlots[index][field] = value;
    setTimeSlots(updatedSlots);
  };

  const addTimeSlot = () => {
    setTimeSlots([...timeSlots, { startTime: "", endTime: "" }]);
  };

  const removeTimeSlot = (index) => {
    const updatedSlots = timeSlots.filter((_, i) => i !== index);
    setTimeSlots(updatedSlots);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSuccess(null);

    try {
      const token = await getAccessTokenSilently({
        audience: "http://localhost:5000/api/v3",
      });

      const res = await axios.post(
        `${import.meta.env.VITE_BASE_URL}/Bus/createbus`,
        { name, deviceID, from, to, timeSlots, ticketPrice },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess(t("createBus.successMessage"));
      setDeviceID("");
      setFrom("");
      setTo("");
      setName("");
      setFromSearchQuery("");
      setToSearchQuery("");
      setTimeSlots([{ startTime: "", endTime: "" }]);
      toast(res.data.message);
      navigate("/Bus");
    } catch (error) {
      console.error("Error creating bus:", error);
      setSuccess(t("createBus.errorMessage"));
      const errorMessage =
        error.response?.data?.message || error.message || t("createBus.genericError");
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Handle "From" search
  const handleFromSearchChange = async (e) => {
    const value = e.target.value;
    setFromSearchQuery(value);

    if (value.length < 3) {
      setFromSuggestions([]);
      setShowFromSuggestions(false);
      return;
    }

    try {
      const res = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
          value
        )}&addressdetails=1&limit=5`
      );
      const data = await res.json();
      setFromSuggestions(data);
      setShowFromSuggestions(true);
    } catch (error) {
      console.error("Search failed:", error);
    }
  };

  // Handle "To" search
  const handleToSearchChange = async (e) => {
    const value = e.target.value;
    setToSearchQuery(value);

    if (value.length < 3) {
      setToSuggestions([]);
      setShowToSuggestions(false);
      return;
    }

    try {
      const res = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
          value
        )}&addressdetails=1&limit=5`
      );
      const data = await res.json();
      setToSuggestions(data);
      setShowToSuggestions(true);
    } catch (error) {
      console.error("Search failed:", error);
    }
  };

  // Handle "From" suggestion click
  const handleFromSuggestionClick = (place) => {
    setFrom(place.display_name);
    setFromSearchQuery(place.display_name);
    setFromSuggestions([]);
    setShowFromSuggestions(false);
  };

  // Handle "To" suggestion click
  const handleToSuggestionClick = (place) => {
    setTo(place.display_name);
    setToSearchQuery(place.display_name);
    setToSuggestions([]);
    setShowToSuggestions(false);
  };

  return (
    <div
      className={`min-h-screen ${
        darktheme
          ? "bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900"
          : "bg-gradient-to-br from-green-50 via-white to-green-100"
      }`}
    >
      <Navbar />

      <main className="max-w-7xl mx-auto px-4 py-8">
        <Card
          className={`max-w-md mx-auto mt-6 shadow-xl rounded-2xl border ${
            darktheme
              ? "bg-gray-800 border-gray-700"
              : "bg-white border-green-100"
          }`}
        >
          <CardHeader>
            <CardTitle
              className={`text-2xl font-bold text-center ${
                darktheme ? "text-white" : "text-gray-800"
              }`}
            >
              {t("createBus.pageTitle")}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <form className="space-y-4" onSubmit={handleSubmit}>
              {/* Bus Name */}
              <div>
                <Label
                  htmlFor="name"
                  className={`block text-sm font-medium mb-2 ${
                    darktheme ? "text-gray-300" : "text-gray-700"
                  }`}
                >
                  {t("createBus.busName")}
                </Label>
                <Input
                  id="name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder={t("createBus.busNamePlaceholder")}
                  className={`w-full p-4 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent transition-all ${
                    darktheme
                      ? "bg-gray-700 border-gray-600 text-white placeholder-gray-400"
                      : "bg-white border-gray-300 text-gray-900"
                  }`}
                  required
                />
              </div>

              {/* Device ID */}
              <div>
                <Label
                  htmlFor="deviceID"
                  className={`block text-sm font-medium mb-2 ${
                    darktheme ? "text-gray-300" : "text-gray-700"
                  }`}
                >
                  {t("createBus.deviceID")}
                </Label>
                <Input
                  id="deviceID"
                  value={deviceID}
                  onChange={(e) => setDeviceID(e.target.value)}
                  placeholder={t("createBus.deviceIDPlaceholder")}
                  className={`w-full p-4 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent transition-all ${
                    darktheme
                      ? "bg-gray-700 border-gray-600 text-white placeholder-gray-400"
                      : "bg-white border-gray-300 text-gray-900"
                  }`}
                  required
                />
              </div>

              {/* Ticket Price */}
              <div>
                <Label
                  htmlFor="ticket-Price"
                  className={`block text-sm font-medium mb-2 ${
                    darktheme ? "text-gray-300" : "text-gray-700"
                  }`}
                >
                  {t("createBus.ticketPrice")}
                </Label>
                <Input
                  id="ticket-Price"
                  value={ticketPrice}
                  onChange={(e) => setticketPrice(e.target.value)}
                  placeholder={t("createBus.ticketPricePlaceholder")}
                  className={`w-full p-4 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent transition-all ${
                    darktheme
                      ? "bg-gray-700 border-gray-600 text-white placeholder-gray-400"
                      : "bg-white border-gray-300 text-gray-900"
                  }`}
                  required
                />
              </div>

              {/* From Location */}
              <div>
                <div
                  className={`rounded-2xl p-6 shadow-lg border ${
                    darktheme
                      ? "bg-gray-700 border-gray-600"
                      : "bg-white border-gray-200"
                  }`}
                >
                  <div className="flex items-center gap-2 mb-5">
                    <span className="text-xl">üîç</span>
                    <h2
                      className={`text-xl font-bold ${
                        darktheme ? "text-white" : "text-gray-800"
                      }`}
                    >
                      {t("createBus.from")}
                    </h2>
                  </div>

                  <div className="relative">
                    <input
                      type="text"
                      value={fromSearchQuery}
                      onChange={handleFromSearchChange}
                      placeholder={t("createBus.fromPlaceholder")}
                      className={`w-full border-2 p-4 pr-12 rounded-xl focus:outline-none focus:border-blue-500 transition-all ${
                        darktheme
                          ? "bg-gray-800 border-gray-600 text-white placeholder-gray-400"
                          : "bg-white border-gray-200 text-gray-800 placeholder-gray-400"
                      }`}
                      required
                    />
                    <div
                      className={`absolute right-4 top-1/2 -translate-y-1/2 ${
                        darktheme ? "text-gray-500" : "text-gray-400"
                      }`}
                    >
                      <svg
                        className="w-5 h-5"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                        />
                      </svg>
                    </div>

                    {showFromSuggestions && fromSuggestions.length > 0 && (
                      <ul
                        className={`absolute border-2 rounded-xl shadow-2xl w-full mt-2 max-h-64 overflow-y-auto z-50 ${
                          darktheme
                            ? "bg-gray-800 border-gray-600"
                            : "bg-white border-gray-200"
                        }`}
                      >
                        {fromSuggestions.map((place, index) => (
                          <li
                            key={place.place_id}
                            onClick={() => handleFromSuggestionClick(place)}
                            className={`p-4 cursor-pointer text-sm border-b last:border-b-0 flex items-start gap-3 transition-colors ${
                              darktheme
                                ? "text-gray-200 border-gray-700 hover:bg-gray-700"
                                : "text-gray-700 border-gray-100 hover:bg-gray-50"
                            }`}
                          >
                            <span className="text-lg mt-0.5">üìç</span>
                            <span className="flex-1">{place.display_name}</span>
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                </div>
              </div>

              {/* To Location */}
              <div>
                <div
                  className={`rounded-2xl p-6 shadow-lg border ${
                    darktheme
                      ? "bg-gray-700 border-gray-600"
                      : "bg-white border-gray-200"
                  }`}
                >
                  <div className="flex items-center gap-2 mb-5">
                    <span className="text-xl">üîç</span>
                    <h2
                      className={`text-xl font-bold ${
                        darktheme ? "text-white" : "text-gray-800"
                      }`}
                    >
                      {t("createBus.to")}
                    </h2>
                  </div>

                  <div className="relative">
                    <input
                      type="text"
                      value={toSearchQuery}
                      onChange={handleToSearchChange}
                      placeholder={t("createBus.toPlaceholder")}
                      className={`w-full border-2 p-4 pr-12 rounded-xl focus:outline-none focus:border-blue-500 transition-all ${
                        darktheme
                          ? "bg-gray-800 border-gray-600 text-white placeholder-gray-400"
                          : "bg-white border-gray-200 text-gray-800 placeholder-gray-400"
                      }`}
                      required
                    />
                    <div
                      className={`absolute right-4 top-1/2 -translate-y-1/2 ${
                        darktheme ? "text-gray-500" : "text-gray-400"
                      }`}
                    >
                      <svg
                        className="w-5 h-5"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                        />
                      </svg>
                    </div>

                    {showToSuggestions && toSuggestions.length > 0 && (
                      <ul
                        className={`absolute border-2 rounded-xl shadow-2xl w-full mt-2 max-h-64 overflow-y-auto z-50 ${
                          darktheme
                            ? "bg-gray-800 border-gray-600"
                            : "bg-white border-gray-200"
                        }`}
                      >
                        {toSuggestions.map((place, index) => (
                          <li
                            key={place.place_id}
                            onClick={() => handleToSuggestionClick(place)}
                            className={`p-4 cursor-pointer text-sm border-b last:border-b-0 flex items-start gap-3 transition-colors ${
                              darktheme
                                ? "text-gray-200 border-gray-700 hover:bg-gray-700"
                                : "text-gray-700 border-gray-100 hover:bg-gray-50"
                            }`}
                          >
                            <span className="text-lg mt-0.5">üìç</span>
                            <span className="flex-1">{place.display_name}</span>
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                </div>
              </div>

              {/* Time Slots */}
              <div>
                <Label
                  className={`block text-sm font-medium mb-2 ${
                    darktheme ? "text-gray-300" : "text-gray-700"
                  }`}
                >
                  {t("createBus.timeSlots")}
                </Label>
                {timeSlots.map((slot, index) => (
                  <div key={index} className="flex gap-2 mb-2 items-center">
                    <Input
                      type="time"
                      value={slot.startTime}
                      onChange={(e) =>
                        handleTimeSlotChange(index, "startTime", e.target.value)
                      }
                      required
                      className={`flex-1 p-3 border rounded-xl ${
                        darktheme
                          ? "bg-gray-700 border-gray-600 text-white"
                          : "bg-white border-gray-300 text-gray-900"
                      }`}
                    />
                    <span
                      className={darktheme ? "text-gray-400" : "text-gray-600"}
                    >
                      {t("createBus.to")}
                    </span>
                    <Input
                      type="time"
                      value={slot.endTime}
                      onChange={(e) =>
                        handleTimeSlotChange(index, "endTime", e.target.value)
                      }
                      required
                      className={`flex-1 p-3 border rounded-xl ${
                        darktheme
                          ? "bg-gray-700 border-gray-600 text-white"
                          : "bg-white border-gray-300 text-gray-900"
                      }`}
                    />
                    {timeSlots.length > 1 && (
                      <Button
                        type="button"
                        onClick={() => removeTimeSlot(index)}
                        className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-lg"
                      >
                        {t("createBus.remove")}
                      </Button>
                    )}
                  </div>
                ))}
                <Button
                  type="button"
                  onClick={addTimeSlot}
                  className="mt-2 w-full bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-xl"
                >
                  {t("createBus.addTimeSlot")}
                </Button>
              </div>

              {/* Submit */}
              <Button
                type="submit"
                disabled={loading}
                className="w-full px-8 py-4 rounded-xl font-medium transition-all duration-300 shadow-lg bg-gradient-to-r from-green-500 to-green-600 text-white hover:from-green-600 hover:to-green-700 hover:shadow-xl disabled:bg-gray-300 disabled:text-gray-500"
              >
                {loading ? t("createBus.creating") : t("createBus.createButton")}
              </Button>
            </form>

            {success && (
              <p
                className={`mt-4 text-center font-medium ${
                  darktheme ? "text-green-400" : "text-green-700"
                }`}
              >
                {success}
              </p>
            )}
          </CardContent>
        </Card>

        {/* Footer */}
        <footer className="mt-16 text-center text-sm">
          <p className={darktheme ? "text-gray-500" : "text-gray-500"}>
            {t("createBus.footer")}
          </p>
        </footer>
      </main>
    </div>
  );
};

export default CreateBus;

import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  User,
  CreditCard,
  Calendar,
  Mail,
  Shield,
  Edit3,
  Save,
  X,
  Clock,
  Hash,
} from "lucide-react";
import axios from "axios";
import { useAuth0 } from "@auth0/auth0-react";
import { useTranslation } from "react-i18next";
import { setuser } from "../../Redux/auth.reducer";
import Navbar from "../shared/Navbar";
import { toast } from "sonner";

const Profile = () => {
  const { usere, darktheme } = useSelector((store) => store.auth);
  const { getAccessTokenSilently } = useAuth0();
  const { t } = useTranslation();

  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState(usere?.name || "");
  const [licenceId, setLicenceId] = useState(usere?.licenceId || "");
  const [driverExp, setDriverExp] = useState(usere?.driverExp || "");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const dispatch = useDispatch();

  if (!usere) {
    return (
      <div
        className={`min-h-screen ${
          darktheme
            ? "bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900"
            : "bg-gradient-to-br from-green-50 via-white to-green-100"
        }`}
      >
        <Navbar />
        <div className="flex items-center justify-center py-16">
          <p
            className={`text-center text-lg ${
              darktheme ? "text-gray-400" : "text-gray-600"
            }`}
          >
            {t("profile.noUserData")}
          </p>
        </div>
      </div>
    );
  }

  const handleUpdate = async () => {
    setLoading(true);
    setMessage("");
    try {
      const token = await getAccessTokenSilently({
        audience: "http://localhost:5000/api/v3",
      });
      console.log(token);
      const res = await axios.put(
        `${import.meta.env.VITE_BASE_URL}/driver/update/profile`,
        { name, licenceId, driverExp },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      dispatch(setuser(res.data.newDetails));
      setMessage(t("profile.updateSuccess"));
      setIsEditing(false);
      toast(res.data.message);
    } catch (err) {
      console.error(err);
      setMessage(t("profile.updateFailed"));
      const errorMessage =
        err.response?.data?.message || err.message || t("profile.updateError");
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className={`min-h-screen ${
        darktheme
          ? "bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900"
          : "bg-gradient-to-br from-green-50 via-white to-green-100"
      }`}
    >
      <Navbar />

      <main className="max-w-7xl mx-auto px-4 py-8">
        {/* Header Section */}

        <div className="max-w-2xl mx-auto">
          <Card
            className={`shadow-xl rounded-2xl border ${
              darktheme
                ? "bg-gray-800/80 border-gray-700 backdrop-blur-sm"
                : "bg-white/80 border-green-100 backdrop-blur-sm"
            }`}
          >
            {/* Profile Header */}
            <CardHeader className="flex flex-col items-center space-y-4 pb-6">
              <div className="relative">
                <Avatar
                  className={`w-32 h-32 border-4 shadow-lg ${
                    darktheme ? "border-green-700" : "border-green-200"
                  }`}
                >
                  {usere.picture ? (
                    <AvatarImage
                      src={usere.picture}
                      alt={usere.name}
                      className="object-cover"
                    />
                  ) : (
                    <AvatarFallback
                      className={`text-3xl font-bold ${
                        darktheme
                          ? "bg-green-900 text-green-400"
                          : "bg-green-100 text-green-700"
                      }`}
                    >
                      {usere.name.charAt(0)}
                    </AvatarFallback>
                  )}
                </Avatar>
                <div
                  className={`absolute -bottom-2 -right-2 w-8 h-8 bg-green-500 rounded-full flex items-center justify-center border-4 ${
                    darktheme ? "border-gray-800" : "border-white"
                  }`}
                >
                  <User className="w-4 h-4 text-white" />
                </div>
              </div>
              <div className="text-center">
                <CardTitle
                  className={`text-2xl font-bold mb-1 ${
                    darktheme ? "text-white" : "text-gray-800"
                  }`}
                >
                  {usere.name}
                </CardTitle>
                <div
                  className={`flex items-center justify-center ${
                    darktheme ? "text-gray-400" : "text-gray-600"
                  }`}
                >
                  <Mail className="w-4 h-4 mr-2" />
                  <span>{usere.email}</span>
                </div>
              </div>
            </CardHeader>
          </Card>

          {/* Additional Info Cards */}
        </div>

        {/* Footer */}
        <footer className="mt-16 text-center text-sm">
          <p className={darktheme ? "text-gray-500" : "text-gray-500"}>
            {t("profile.copyright")}
          </p>
        </footer>
      </main>
    </div>
  );
};

export default Profile;

import React, { useState } from "react";
import MicInput from "./MicInput";
import { MapContainer, Marker, TileLayer, useMapEvents } from "react-leaflet";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useAuth0 } from "@auth0/auth0-react";
import { useTranslation } from "react-i18next";
import Navbar from "../shared/Navbar";
import { toast } from "sonner";
import { useSelector } from "react-redux";
import L from "leaflet";

const GEOCODE_API = "https://nominatim.openstreetmap.org/search";
const markerIcon = new L.Icon({
  iconUrl: "https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const LocationPicker = ({ onSelect }) => {
  useMapEvents({
    click(e) {
      onSelect({ lat: e.latlng.lat, lon: e.latlng.lng });
    },
  });
  return null;
};

const PlaceSearch = ({ label, onSelect, enableUseMyLocation = false }) => {
  const [query, setQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingLocation, setLoadingLocation] = useState(false);
  const [selectedPos, setSelectedPos] = useState(null);
  const { darktheme } = useSelector((store) => store.auth);
  const { t } = useTranslation();

  const handleSearch = async (value) => {
    setQuery(value);
    if (value.length < 3) return setSuggestions([]);

    try {
      setLoading(true);
      const res = await fetch(
        `${GEOCODE_API}?q=${encodeURIComponent(value)}&format=json&limit=5`
      );
      const data = await res.json();
      setSuggestions(data);
    } catch (err) {
      console.error("Geocode error:", err);
    } finally {
      setLoading(false);
    }
  };

  const reverseGeocode = async (lat, lon) => {
    try {
      const res = await fetch(
        `${GEOCODE_API}?format=json&lat=${lat}&lon=${lon}&zoom=16&addressdetails=1`
      );
      const data = await res.json();
      return data[0]?.display_name || `${lat}, ${lon}`;
    } catch (err) {
      console.error("Reverse geocoding failed", err);
      return `${lat}, ${lon}`;
    }
  };

  const handleUseMyLocation = async () => {
    if (!navigator.geolocation) {
      alert(t("payment.geolocationNotSupported"));
      return;
    }

    setLoadingLocation(true);
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        try {
          const address = await reverseGeocode(latitude, longitude);

          setQuery(address);
          setSelectedPos({ lat: latitude, lon: longitude });
          onSelect({ lat: latitude, lon: longitude, address });
          setSuggestions([]);
        } finally {
          setLoadingLocation(false);
        }
      },
      (err) => {
        console.error("Geolocation error", err);
        alert(t("payment.unableToGetLocation"));
        setLoadingLocation(false);
      }
    );
  };

  return (
    <div className="mb-4 relative">
      <label
        className={`block mb-1 font-medium ${
          darktheme ? "text-gray-300" : "text-gray-700"
        }`}
      >
        {label}
      </label>

      <MicInput
        type="text"
        value={query}
        placeholder={t("payment.typePlaceholder")}
        onChange={(e) => handleSearch(e.target.value)}
        className={`border p-2 w-full rounded ${
          darktheme
            ? "bg-gray-700 border-gray-600 text-white placeholder-gray-400"
            : "bg-white border-gray-300 text-gray-900"
        }`}
      />

      {enableUseMyLocation && (
        <button
          type="button"
          className={`text-sm mt-1 underline ${
            darktheme ? "text-blue-400" : "text-blue-600"
          }`}
          onClick={handleUseMyLocation}
          disabled={loadingLocation}
        >
          {loadingLocation ? t("payment.gettingLocation") : t("payment.useMyLocation")}
        </button>
      )}

      {loading && (
        <p
          className={`text-sm mt-1 ${
            darktheme ? "text-gray-500" : "text-gray-400"
          }`}
        >
          {t("payment.searching")}
        </p>
      )}

      {suggestions.length > 0 && (
        <ul
          className={`absolute z-10 w-full shadow rounded mt-1 max-h-40 overflow-y-auto ${
            darktheme
              ? "bg-gray-800 border border-gray-600"
              : "bg-white border border-gray-200"
          }`}
        >
          {suggestions.map((s, idx) => (
            <li
              key={idx}
              className={`p-2 cursor-pointer text-sm ${
                darktheme
                  ? "text-gray-200 hover:bg-gray-700"
                  : "text-gray-900 hover:bg-gray-100"
              }`}
              onClick={() => {
                const pos = { lat: parseFloat(s.lat), lon: parseFloat(s.lon) };
                setQuery(s.display_name);
                setSelectedPos(pos);
                onSelect({ ...pos, address: s.display_name });
                setSuggestions([]);
              }}
            >
              {s.display_name}
            </li>
          ))}
        </ul>
      )}

      {selectedPos && (
        <div className="mt-4 h-64">
          <MapContainer
            center={[selectedPos.lat, selectedPos.lon]}
            zoom={15}
            style={{ height: "100%", width: "100%" }}
          >
            <TileLayer
              url={
                darktheme
                  ? "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png"
                  : "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              }
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <Marker
              position={[selectedPos.lat, selectedPos.lon]}
              icon={markerIcon}
            />
            <LocationPicker
              onSelect={async (pos) => {
                setSelectedPos(pos);
                const address = await reverseGeocode(pos.lat, pos.lon);
                setQuery(address);
                onSelect({ ...pos, address });
              }}
            />
          </MapContainer>
        </div>
      )}
    </div>
  );
};

